mapper.select:
SELECT * FROM( SELECT A.CBNO AS CBNO, A.CBSUBJECT AS CBSUBJECT, A.CBNAME AS CBNAME, A.CBCONTENT AS CBCONTENT, A.ACADEMY_ANO AS ACADEMY_ANO, A.MEMBER_MNO AS MEMBER_MNO, A.CDELETEYN AS CDELETEYN, TO_CHAR(A.CINSERTDATE, 'YYYY-MM-DD') AS CINSERTDATE, TO_CHAR(A.CUPDATEDATE, 'YYYY-MM-DD') AS CUPDATEDATE, CEIL(ROW_NUMBER() OVER(ORDER BY A.CBNO DESC)/#{pagesize, jdbcType=VARCHAR}) PAGENO, COUNT(A.CBNO) OVER() AS TOTALCOUNT, #{curpage, jdbcType=VARCHAR} AS CURPAGE, #{pagesize, jdbcType=VARCHAR} AS PAGESIZE, #{groupsize, jdbcType=VARCHAR} AS GROUPSIZE, #{pno, jdbcType=VARCHAR} AS PNO, B.ANO AS ANO, B.ANAME AS ANAME FROM CONSULTBOARD A, PAGING P, ACADEMY B WHERE CDELETEYN = 'N' AND PNO='P003' AND A.ACADEMY_ANO = B.ANO
AND TO_CHAR(A.CINSERTDATE,'YYYY-MM-DD') >= #{startDate,jdbcType=VARCHAR} AND TO_CHAR(A.CINSERTDATE,'YYYY-MM-DD') <= #{endDate,jdbcType=VARCHAR}
AND CBSUBJECT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
AND CBCONTENT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
AND CBSUBJECT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%' OR CBCONTENT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
ORDER BY 1 DESC ) WHERE PAGENO=#{curpage, jdbcType=VARCHAR}

mapper.update:
UPDATE CONSULTBOARD
SET CBSUBJECT=#{cbsubject, jdbcType=VARCHAR},
    CBCONTENT=#{cbcontent, jdbcType=VARCHAR}
WHERE CBNO=#{cbno, jdbcType=VARCHAR}
AND   CDELETEYN='N'
