mapper.select:
SELECT * FROM( SELECT A.FBNO AS FBNO, A.FBSUBJECT AS FBSUBJECT, A.FBNAME AS FBNAME, A.FBIMG AS FBIMG, A.FBCONTENT AS FBCONTENT, A.MEMBER_MNO AS MEMBER_MNO, A.FBDELETEYN AS FBDELETEYN, TO_CHAR(A.FBINSERTDATE,'YYYY-MM-DD') AS FBINSERTDATE, TO_CHAR(A.FBUPDATEDATE,'YYYY-MM-DD') AS FBUPDATEDATE, CEIL(ROW_NUMBER() OVER(ORDER BY A.FBNO DESC)/#{pagesize, jdbcType=VARCHAR}) PAGENO, COUNT(A.FBNO) OVER() AS TOTALCOUNT, #{curpage, jdbcType=VARCHAR} AS CURPAGE, #{pagesize, jdbcType=VARCHAR} AS PAGESIZE, #{groupsize, jdbcType=VARCHAR} AS GROUPSIZE, #{pno, jdbcType=VARCHAR} AS PNO FROM FREEBOARD A, PAGING B WHERE A.FBDELETEYN ='N' AND PNO='P001'
AND TO_CHAR(A.FBINSERTDATE,'YYYY-MM-DD') >= #{startDate,jdbcType=VARCHAR} AND TO_CHAR(A.FBINSERTDATE,'YYYY-MM-DD') <= #{endDate,jdbcType=VARCHAR}
AND FBSUBJECT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
AND FBCONTENT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
AND FBSUBJECT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%' OR FBCONTENT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
ORDER BY 1 DESC )WHERE PAGENO=#{curpage, jdbcType=VARCHAR}
