mapper.select:
SELECT * FROM( SELECT A.RBNO AS RBNO, A.RBSUBJECT AS RBSUBJECT, A.RBNAME AS RBNAME, A.RBIMG AS RBIMG, A.RBCONTENT AS RBCONTENT, A.ACADEMY_ANO AS ACADEMY_ANO, A.MEMBER_MNO AS MEMBER_MNO, A.RBGRADE AS RBGRADE, A.RBDELETEYN AS RBDELETEYN, TO_CHAR(A.RBINSERTDATE,'YYYY-MM-DD') AS RBINSERTDATE, TO_CHAR(A.RBUPDATEDATE,'YYYY-MM-DD') AS RBUPDATEDATE, CEIL(ROW_NUMBER() OVER(ORDER BY A.RBNO DESC)/#{pagesize, jdbcType=VARCHAR}) PAGENO, COUNT(A.RBNO) OVER() AS TOTALCOUNT, #{curpage, jdbcType=VARCHAR} AS CURPAGE, #{pagesize, jdbcType=VARCHAR} AS PAGESIZE, #{groupsize, jdbcType=VARCHAR} AS GROUPSIZE, #{pno, jdbcType=VARCHAR} AS PNO FROM REVIEWBOARD A, PAGING P WHERE RBDELETEYN = 'N' AND PNO='P002'
AND TO_CHAR(A.RBINSERTDATE,'YYYY-MM-DD') >= #{startDate,jdbcType=VARCHAR} AND TO_CHAR(A.RBINSERTDATE,'YYYY-MM-DD') <= #{endDate,jdbcType=VARCHAR}
AND RBSUBJECT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
AND RBCONTENT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
AND RBSUBJECT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%' OR RBCONTENT LIKE '%' || #{keyword,jdbcType=VARCHAR} || '%'
ORDER BY 1 DESC ) WHERE PAGENO=#{curpage, jdbcType=VARCHAR}

mapper.update:
UPDATE REVIEWBOARD
SET RBSUBJECT =#{rbsubject, jdbcType=VARCHAR},
    RBCONTENT =#{rbcontent, jdbcType=VARCHAR},
    RBIMG=#{rbimg, jdbcType=VARCHAR}
WHERE RBNO=#{rbno, jdbcType=VARCHAR}
